mapper.insert:
INSERT INTO auction_enroll VALUES(
						  ((SELECT *
	  							FROM 
	  								(SELECT IFNULL(MAX(enroll_code),0) FROM auction_enroll) A)+1)
						, #{auction_code}
						, #{id}
						, 'N'
			)

mapper.select:
SELECT * 
	FROM auction_enroll
		WHERE auction_code = #{auction_code} AND member_id = #{id}
