mapper.select:
SELECT a.auction_code , auction_type , auction_item_name , auction_content , auction_present_price , auction_immediate_price , DATE_FORMAT(auction_date, '%Y.%m.%d %H:%i') AS auction_date , DATE_FORMAT(auction_start_date, '%Y.%m.%d %H:%i') AS auction_start_date , DATE_FORMAT(auction_end_date, '%Y.%m.%d %H:%i') AS auction_end_date , DATEDIFF(auction_start_date, now()) AS auction_datediff , auction_status , success_price , auction_notice , auction_category , image_code , table_code , image_name , COALESCE(enroll_count, 0) AS enroll_count -- enroll_count 가 null이면 0 , ROW_NUMBER() OVER(ORDER BY a.auction_date ) AS rownum , count(*) over() AS totalCnt FROM auction a JOIN images i ON a.auction_code = i.table_code LEFT JOIN ( SELECT auction_code, COUNT(*) AS enroll_count FROM auction_enroll GROUP BY auction_code) AS ae ON a.auction_code = ae.auction_code WHERE 1=1
AND (auction_item_name LIKE '%${searchKeyword}%' OR auction_content LIKE '%${searchKeyword}%')
AND auction_type = #{typeSelect}
LIMIT ${startNum}, ${endNum}

mapper.insert:
	INSERT INTO auction_enroll 
				VALUES(
					  ((SELECT *
  							FROM 
  								(SELECT IFNULL(MAX(CAST(enroll_code AS UNSIGNED)) ,0) FROM auction_enroll) A)+1)
					, #{auction_code}
					, #{id}
					, 'N'
				)

mapper.select:
SELECT * 
	FROM auction_enroll
		WHERE auction_code = #{auction_code} AND member_id = #{id}

mapper.insert:
INSERT 
	INTO auction_log 
	VALUES (
		(SELECT *
			FROM (SELECT IFNULL(MAX(CAST(log_code  AS UNSIGNED)),0) +1
	 			FROM auction_log) A)
			, #{auctionCode}
			, #{id}
			, #{messages}
			, now()
			)

mapper.select:
SELECT *
	FROM auction_log
		WHERE member_id = #{id}
		and auction_code = #{auction_code}
			ORDER BY log_time DESC LIMIT 1

mapper.update:
UPDATE auction 
	SET auction_type = '경매 완료'
		, auction_status = #{success_id}
		, success_price = #{success_price}
	WHERE 
		auction_code = #{auction_code}
